import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8080';

const chatService = {
  sendMessage: async (message) => {
    try {
      const response = await axios.post(`${API_BASE_URL}/api/chat/message`, {
        message,
        context: 'portfolio_assistant' // Add context for better response handling
      });
      return response.data.response;
    } catch (error) {
      console.error('Chat service error:', error);
      // Fallback to natural-sounding responses if API fails
      return getNaturalResponse(message);
    }
  }
};

const getNaturalResponse = (message) => {
  const lowerMessage = message.toLowerCase();
  const responses = [
    {
      keywords: ['project', 'built', 'created', 'developed'],
      responses: [
        "Oh, I'd love to tell you about some of my favorite projects! I recently built an AI Meeting Summarizer that uses Whisper and Hugging Face to automatically generate meeting notes. It's been a game-changer for productivity.",
        "I'm particularly proud of the AI Voice Agent I developed with RAG and TTS technology. It can answer complex questions by searching through documents - kind of like having a personal research assistant!",
        "One project close to my heart is BloodLink, which connects blood donors with those in need. It's amazing to see technology making a real difference in people's lives."
      ]
    },
    {
      keywords: ['skill', 'technologies', 'tech stack', 'what can you do'],
      responses: [
        "I've been working with Python and JavaScript for a while now - they're like second nature to me. Lately, I've been diving deep into AI/ML with TensorFlow and PyTorch, especially for building RAG systems.",
        "My tech toolkit is pretty diverse! From frontend frameworks like React to cloud platforms like Azure and GCP, I enjoy working across the stack. I've been focusing a lot on AI engineering and growth engineering recently.",
        "Would you believe I started with basic web development? Now I'm building AI agents and fine-tuning LLMs. It's been quite the journey from HTML to machine learning models!"
      ]
    },
    {
      keywords: ['experience', 'work', 'job', 'career', 'background'],
      responses: [
        "Currently, I'm working as a Growth Engineer at Nella Marketing, where I develop AI agents to automate lead generation. It's fascinating to see how AI can transform business processes!",
        "My career path has been quite the adventure! From being an Account Executive to a Web Developer, and now working with AI - each role has given me unique perspectives that I bring to my work.",
        "I'm actually still studying for my Bachelor's in Computer Applications at Amrita Vishwa Vidyapeetham. It's been challenging to balance work and studies, but I love applying what I learn in class to real-world projects."
      ]
    },
    {
      keywords: ['who are you', 'introduce', 'tell me about yourself', 'what do you do'],
      responses: [
        "I'm Prajwal's AI assistant! I help showcase his work and answer questions about his projects and experience. He's an AI Developer and Growth Engineer who's passionate about building innovative solutions. What would you like to know more about?",
        "Hi there! I'm here to tell you about Prajwal's work in AI and software development. He's currently focused on creating intelligent systems and automation tools. Is there something specific you're curious about?",
        "I'm your friendly guide to Prajwal's professional journey! From AI development to growth engineering, there's a lot to explore. What would you like to dive into first?"
      ]
    }
  ];

  // Find a matching response category
  for (const category of responses) {
    if (category.keywords.some(keyword => lowerMessage.includes(keyword))) {
      const randomIndex = Math.floor(Math.random() * category.responses.length);
      return category.responses[randomIndex];
    }
  }

  // Default responses if no keywords match
  const defaultResponses = [
    "That's an interesting question! I'd be happy to share more about Prajwal's work. Would you like to know about his projects, skills, or experience?",
    "I'd love to help with that! Could you tell me if you're interested in learning about specific projects, technical skills, or work experience?",
    "Hmm, I'm not sure I caught that. Could you rephrase? I can tell you about Prajwal's work in AI development, his technical skills, or his professional background.",
    "I'm here to help you learn more about Prajwal's work. What would you like to know? You can ask about his projects, technical expertise, or professional journey."
  ];

  return defaultResponses[Math.floor(Math.random() * defaultResponses.length)];
};

export default chatService;
