import axios from 'axios';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:8080';

const chatService = {
  sendMessage: async (message) => {
    try {
      const response = await axios.post(`${API_BASE_URL}/api/chat/message`, {
        message
      });
      return response.data.response;
    } catch (error) {
      console.error('Chat service error:', error);
      // Fallback to local responses if API fails
      return getFallbackResponse(message);
    }
  }
};

const getFallbackResponse = (message) => {
  const lowerMessage = message.toLowerCase();

  if (lowerMessage.includes('project')) {
    return "I've worked on several exciting projects including an AI Meeting Summarizer using Whisper and Hugging Face, an AI Voice Agent with RAG & TTS, BloodLink for connecting blood donors, and a Student Enrollment Prediction system using ML algorithms.";
  }

  if (lowerMessage.includes('skill')) {
    return "My technical skills include Python, Java, JavaScript, React, AI/ML frameworks (TensorFlow, PyTorch), LLM fine-tuning, RAG systems, cloud platforms (Azure, GCP), and various development tools. I specialize in AI engineering and growth engineering.";
  }

  if (lowerMessage.includes('experience') || lowerMessage.includes('work')) {
    return "I'm currently a Growth Engineer at Nella Marketing where I develop AI agents for lead automation. I've also worked as a Web Developer at Godofon Events and Account Executive at Andro Consultant. I'm pursuing my Bachelor's in Computer Applications at Amrita Vishwa Vidyapeetham.";
  }

  return "I'm Prajwal Prasad, an AI Developer and Growth Engineer passionate about building innovative AI solutions. Feel free to ask about my projects, skills, education, or experience!";
};

export default chatService;
