import React, { useState, useEffect, useRef } from 'react';

const Typewriter = ({ roles }) => {
  const [currentText, setCurrentText] = useState('');
  const [isDeleting, setIsDeleting] = useState(false);
  const [roleIndex, setRoleIndex] = useState(0);
  const [typingSpeed, setTypingSpeed] = useState(100);
  const containerRef = useRef(null);
  const cursorRef = useRef(null);
  const [containerWidth, setContainerWidth] = useState('auto');

  // Get the current role from the roles array
  const currentRole = roles[roleIndex];

  // Set initial container width based on the longest role
  useEffect(() => {
    if (roles && roles.length > 0) {
      const longestRole = [...roles].sort((a, b) => b.length - a.length)[0];
      const tempSpan = document.createElement('span');
      tempSpan.style.visibility = 'hidden';
      tempSpan.style.position = 'absolute';
      tempSpan.style.fontSize = window.getComputedStyle(document.body).fontSize;
      tempSpan.style.fontFamily = window.getComputedStyle(document.body).fontFamily;
      tempSpan.textContent = longestRole;
      document.body.appendChild(tempSpan);
      setContainerWidth(`${tempSpan.offsetWidth}px`);
      document.body.removeChild(tempSpan);
    }
  }, [roles]);

  // Typewriter effect
  useEffect(() => {
    const pauseTime = 1500; // Time to pause at the end of a word

    const timer = setTimeout(() => {
      if (isDeleting) {
        // Deleting text
        setCurrentText(prev => prev.slice(0, -1));
        setTypingSpeed(50); // Faster when deleting
      } else {
        // Typing text
        setCurrentText(currentRole.substring(0, currentText.length + 1));
        setTypingSpeed(100);
      }

      // Handle transitions between typing and deleting
      if (!isDeleting && currentText === currentRole) {
        // Finished typing, start deleting after pause
        setTimeout(() => setIsDeleting(true), pauseTime);
      } else if (isDeleting && currentText === '') {
        // Finished deleting, move to next role
        setIsDeleting(false);
        setRoleIndex((prevIndex) => (prevIndex + 1) % roles.length);
        setTypingSpeed(100);
      }
    }, typingSpeed);

    return () => clearTimeout(timer);
  }, [currentText, isDeleting, currentRole, roleIndex, roles, typingSpeed]);

  return (
    <span 
      ref={containerRef}
      style={{
        display: 'inline-block',
        minWidth: containerWidth,
        textAlign: 'left',
        position: 'relative'
      }}
    >
      <span style={{ whiteSpace: 'nowrap' }}>
        {currentText}
        <span 
          ref={cursorRef}
          className="cursor" 
          style={{
            display: 'inline-block',
            width: '2px',
            height: '1.2em',
            backgroundColor: 'currentColor',
            marginLeft: '2px',
            verticalAlign: 'middle',
            animation: 'blink 0.8s infinite',
            position: 'relative',
            top: '-1px'
          }}
        />
      </span>
    </span>
  );
};

export default Typewriter;
