import React, { useState, useRef, useEffect } from 'react';
import chatService from '../services/chatService';
import { motion, AnimatePresence } from 'framer-motion';
import { FiSend, FiRefreshCw, FiX, FiMessageSquare, FiUser } from 'react-icons/fi';
import '../styles/chat.css';

const ChatInterface = () => {
  const [messages, setMessages] = useState([
    {
      id: 1,
      type: 'ai',
      content: 'ðŸ‘‹ Hi! I\'m Prajwal\'s AI assistant. Ask me about his projects, skills, experience, or anything else you\'d like to know!',
      timestamp: new Date()
    }
  ]);
  const [input, setInput] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = (smooth = true) => {
    if (messagesEndRef.current) {
      // Use the messages container's scrollTop instead of scrollIntoView
      const messagesContainer = messagesEndRef.current.closest('.chat-messages');
      if (messagesContainer) {
        if (smooth) {
          messagesContainer.scrollTo({
            top: messagesContainer.scrollHeight,
            behavior: 'smooth'
          });
        } else {
          messagesContainer.scrollTop = messagesContainer.scrollHeight;
        }
      }
    }
  };

  // Only scroll to bottom when messages change
  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSend = async (e) => {
    // Prevent default form submission behavior that might cause scrolling
    if (e) {
      e.preventDefault();
      e.stopPropagation();
    }

    if (!input.trim() || isTyping) return;

    const userMessage = {
      id: Date.now(),
      type: 'user',
      content: input,
      timestamp: new Date()
    };

    setMessages(prev => {
      const newMessages = [...prev, userMessage];
      // Scroll to bottom immediately after adding user message
      setTimeout(() => scrollToBottom(), 50);
      return newMessages;
    });
    setInput('');
    setIsTyping(true);

    try {
      const response = await chatService.sendMessage(input);

      setTimeout(() => {
        const aiMessage = {
          id: Date.now() + 1,
          type: 'ai',
          content: response,
          timestamp: new Date()
        };
        setMessages(prev => {
          const newMessages = [...prev, aiMessage];
          // Scroll to bottom after AI response is added
          setTimeout(() => scrollToBottom(), 50);
          return newMessages;
        });
        setIsTyping(false);
      }, 800 + Math.random() * 1000);
    } catch (error) {
      console.error('Error:', error);
      setIsTyping(false);
    }
  };

  const handleQuickReply = (text, e) => {
    if (e) {
      e.preventDefault();
      e.stopPropagation();
    }
    setInput(text);
    handleSend(e);
  };

  return (
    <motion.div 
      className="ai-chat-container"
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.5 }}
    >
      <div className="chat-interface">
        <div className="chat-header">
          <div className="chat-header-left">
            <motion.div 
              className="ai-avatar"
              animate={{ 
                scale: [1, 1.1, 1],
                rotate: [0, 5, -5, 0]
              }}
              transition={{ 
                duration: 2,
                repeat: Infinity,
                repeatType: "reverse"
              }}
            >
              <FiMessageSquare />
              <div className="ai-status"></div>
            </motion.div>
            <div className="chat-header-info">
              <h4>AI Assistant</h4>
              <p>Ask me about Prajwal's portfolio</p>
            </div>
          </div>
          <div className="chat-actions">
            <motion.button 
              className="chat-action-btn" 
              onClick={() => setMessages([messages[0]])}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              <FiRefreshCw size={14} />
              <span>Clear</span>
            </motion.button>
          </div>
        </div>

        <div className="chat-messages">
          <AnimatePresence>
            {messages.map((message, index) => (
              <Message key={`${message.id}-${index}`} message={message} />
            ))}
            {isTyping && <TypingIndicator />}
            <div ref={messagesEndRef} />
          </AnimatePresence>
        </div>

        <div className="chat-input-container">
          <div className="chat-input-wrapper">
            <textarea
              className="chat-input"
              value={input}
              onChange={(e) => {
                setInput(e.target.value);
                e.target.style.height = 'auto';
                e.target.style.height = Math.min(e.target.scrollHeight, 120) + 'px';
              }}
              onKeyDown={(e) => {
                if (e.key === 'Enter' && !e.shiftKey) {
                  e.preventDefault();
                  e.stopPropagation();
                  handleSend(e);
                }
              }}
              placeholder="Ask about my AI projects..."
              rows="1"
              aria-label="Type your message"
            />
            <motion.button 
              className="chat-send"
              onClick={(e) => {
                e.preventDefault();
                e.stopPropagation();
                handleSend(e);
              }}
              disabled={!input.trim() || isTyping}
              aria-label="Send message"
              whileHover={{ scale: 1.1 }}
              whileTap={{ scale: 0.95 }}
            >
              <FiSend className="send-icon" />
            </motion.button>
          </div>
          <QuickReplies onQuickReply={handleQuickReply} />
        </div>
      </div>
    </motion.div>
  );
};

const Message = React.memo(({ message }) => {
  const formatTime = (date) => {
    return date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  return (
    <motion.div 
      className={`message ${message.type}`}
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.3 }}
      layout
    >
      <div className="message-avatar">
        {message.type === 'user' ? (
          <FiUser className="avatar-icon" />
        ) : (
          <FiMessageSquare className="avatar-icon ai" />
        )}
      </div>
      <div className="message-wrapper">
        <div className="message-content">
          {message.content}
        </div>
        <div className="message-time">
          {formatTime(message.timestamp)}
        </div>
      </div>
    </motion.div>
  );
});

const TypingIndicator = () => (
  <motion.div 
    className="typing-message"
    initial={{ opacity: 0, y: 10 }}
    animate={{ opacity: 1, y: 0 }}
    transition={{ duration: 0.3 }}
  >
    <div className="message-avatar">
      <FiMessageSquare className="avatar-icon ai" />
    </div>
    <div className="typing-content">
      <div className="typing-indicator">
        <motion.span
          animate={{
            y: [0, -5, 0],
          }}
          transition={{
            duration: 1.5,
            repeat: Infinity,
            repeatType: "loop",
          }}
        />
        <motion.span
          animate={{
            y: [0, -5, 0],
          }}
          transition={{
            duration: 1.5,
            repeat: Infinity,
            repeatType: "loop",
            delay: 0.2,
          }}
        />
        <motion.span
          animate={{
            y: [0, -5, 0],
          }}
          transition={{
            duration: 1.5,
            repeat: Infinity,
            repeatType: "loop",
            delay: 0.4,
          }}
        />
      </div>
      <span className="typing-text">typing...</span>
    </div>
  </motion.div>
);

const QuickReplies = ({ onQuickReply }) => {
  const quickReplies = [
    "Tell me about projects",
    "What are your skills?",
    "Work experience",
    "Contact info"
  ];

  // Check if we're on mobile
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 768);
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  return (
    <div className="quick-replies">
      {quickReplies.map(reply => (
        <button
          key={reply}
          className="quick-reply-btn"
          onClick={(e) => onQuickReply(reply, e)}
          type="button"
        >
          {isMobile && reply.length > 15 ? reply.substring(0, 15) + '...' : reply}
        </button>
      ))}
    </div>
  );
};

export default ChatInterface;
