import React, { useState, useEffect } from 'react';
import '../styles/components.css';

const Navigation = () => {
  const [scrolled, setScrolled] = useState(false);
  const [menuOpen, setMenuOpen] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 50);
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const toggleMenu = () => {
    if (menuOpen) {
      // Add closing animation class
      document.querySelector('.nav-links')?.classList.add('closing');
      document.querySelector('.nav-overlay')?.classList.add('closing');

      // Wait for animation to complete before updating state
      setTimeout(() => {
        setMenuOpen(false);
        document.body.style.overflow = 'auto';
      }, 300); // Match this with CSS transition duration
    } else {
      setMenuOpen(true);
      document.body.style.overflow = 'hidden';
    }
  };

  // Close menu when clicking on a navigation link or overlay
  const closeMenu = () => {
    if (!menuOpen) return;

    // Add closing animation class
    document.querySelector('.nav-links')?.classList.add('closing');
    document.querySelector('.nav-overlay')?.classList.add('closing');

    // Wait for animation to complete before updating state
    setTimeout(() => {
      setMenuOpen(false);
      document.body.style.overflow = 'auto';
    }, 300); // Match this with CSS transition duration
  };

  // Close menu when clicking outside
  useEffect(() => {
    const handleClickOutside = (e) => {
      if (menuOpen && !e.target.closest('.nav-links') && !e.target.closest('.menu-toggle')) {
        closeMenu();
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [menuOpen]);

  return (
    <>
      <nav id="navbar" className={`${scrolled ? 'scrolled' : ''} ${menuOpen ? 'menu-open' : ''}`}>
        <div className="nav-container">
          <div className="logo"><a href="#home">Prajwal Prasad</a></div>
          <button className="menu-toggle" onClick={toggleMenu} aria-label="Toggle menu">
            <span className="hamburger"></span>
          </button>
          <ul className={`nav-links ${menuOpen ? 'active' : ''}`}>
            <li><a href="#home" onClick={closeMenu}>Home</a></li>
            <li><a href="#about" onClick={closeMenu}>About</a></li>
            <li><a href="#resume" onClick={closeMenu}>Resume</a></li>
            <li><a href="#projects" onClick={closeMenu}>Projects</a></li>
            <li><a href="#contact" onClick={closeMenu}>Contact</a></li>
          </ul>
        </div>
      </nav>
      <div 
        className={`nav-overlay ${menuOpen ? 'active' : ''}`} 
        onClick={closeMenu}
      />
    </>
  );
};

export default Navigation;
